package programable.android.klerart.showuseronrecyclerview.IMGTryView;import android.content.Context;import android.database.Cursor;import android.database.sqlite.SQLiteDatabase;import android.widget.ListView;import com.readystatesoftware.sqliteasset.SQLiteAssetHelper;import java.util.ArrayList;//this For trying Display IMGpublic class MyDbHandler  extends SQLiteAssetHelper {    public static final String IMAGE_ID = "IMGStudent";    public static final String IMAGE = "image";    final  static String DATABASE_NAME="StudentsFullIMG.sqlite";    final  static Integer DATABASE_VERSION=1;    final  static String DATABASE_TABLE="Student";    public MyDbHandler(Context context) {        super(context, DATABASE_NAME, null, DATABASE_VERSION);    }    public MyDbHandler(Context context, String name, SQLiteDatabase.CursorFactory factory, int version) {        super(context, name, factory, version);    }    //get all img in ListView    public ArrayList<Person>getLi(){        Person person=null;        ArrayList<Person> personArrayList=new ArrayList<>();        SQLiteDatabase db=this.getWritableDatabase();        Cursor cursor=db.rawQuery("select * from "+DATABASE_TABLE,null);        cursor.moveToFirst();        while (!cursor.isAfterLast()){//some thing About IMGStudent            person=new Person(cursor.getBlob(cursor.getColumnIndex(String.valueOf(7))));           personArrayList.add(person);          cursor.moveToNext();        }        return  personArrayList;    }}