package programable.android.klerart.showuseronrecyclerview.IMGTryView;public class Person {    private byte[] IMG;    private String Name;    public Person(byte[] IMG){        this.IMG=IMG;        this.Name=Name;    }    public byte[] getIMG() {        return IMG;    }    public void setIMG(byte[] IMG) {        this.IMG = IMG;    }    public String getName() {        return Name;    }    public void setName(String name) {        Name = name;    }}