package programable.android.klerart.showuseronrecyclerview;import androidx.appcompat.app.AppCompatActivity;import androidx.recyclerview.widget.LinearLayoutManager;import androidx.recyclerview.widget.RecyclerView;import android.content.Intent;import android.os.Bundle;import android.view.Gravity;import android.view.View;import android.widget.Button;import android.widget.Toast;import programable.android.klerart.showuseronrecyclerview.Database.Database;import programable.android.klerart.showuseronrecyclerview.MyAdapter.SearchAdapter;import programable.android.klerart.showuseronrecyclerview.kLerTry.ExtraActivity;import programable.android.klerart.showuseronrecyclerview.kLerTry.InsertDataActivityOrginal_kLer;import programable.android.klerart.showuseronrecyclerview.kLerTry.LogInActivity;import programable.android.klerart.showuseronrecyclerview.Food.MyInsertData;import programable.android.klerart.showuseronrecyclerview.kLerTry.SearchViewCustom;////public class MainActivity extends AppCompatActivity {    Database database;    SearchAdapter adapter;    RecyclerView recyclerView;    Button BtnView2Department,BasicVBtn,button,otherBtn,OtherBtnEx,InsertActivity,UpDataRow,newActivity;    RecyclerView.LayoutManager layoutManager;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        newActivity=findViewById(R.id.newActivity);        newActivity.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                startActivity(new Intent(MainActivity.this, ExtraActivity.class));            }        });        InsertActivity=findViewById(R.id.InsertActivity);        InsertActivity.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                startActivity(new Intent(MainActivity.this, MyInsertData.class));            }        });        button=findViewById(R.id.LogInForum);        button.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {               startActivity(new Intent(MainActivity.this, LogInActivity.class));                //Uri uri=Da;              //  InputStream inputStream=getContentResolver().openInputStream();            }        });        UpDataRow=findViewById(R.id.UpDataRow);        UpDataRow.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {              Toast toast=  Toast.makeText(getApplicationContext(),"Position Center",Toast.LENGTH_LONG);              toast.setGravity(Gravity.CENTER,0,0);              toast.show();            }        });        BasicVBtn=findViewById(R.id.BasicVBtn);        BasicVBtn.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                startActivity(new Intent(MainActivity.this,BasicView.class));            }        });        BtnView2Department=findViewById(R.id.BtnView2Department);        BtnView2Department.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                startActivity(new Intent(MainActivity.this,Main2Activity.class));            }        });        database=new Database(this);        recyclerView=findViewById(R.id.MyRecyleView);        adapter=new SearchAdapter(this,database.getAStudentBayg());        layoutManager=new LinearLayoutManager(this);        recyclerView.setLayoutManager(layoutManager);        recyclerView.setHasFixedSize(true);        recyclerView.setAdapter(adapter);        ////////////////////////////////////////////////////////////        otherBtn=findViewById(R.id.OtherBtn);        OtherBtnEx=findViewById(R.id.OtherBtnEx);        OtherBtnEx.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view)            {                startActivity(new Intent(MainActivity.this, InsertDataActivityOrginal_kLer.class));            }        });        otherBtn.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                startActivity(new Intent(MainActivity.this, SearchViewCustom.class));            }        });//        recyclerView.addOnItemTouchListener();        //recyclerView.setOnFlingListener(new AdapterView.AdapterContextMenuInfo(int ));    }}