package programable.android.klerart.showuseronrecyclerview.Modell;public class Student {    String StuName;    int StuSemester,StuGroup,StuDepartment,StudentId;    byte[] Imga;    public Student(String stuName, int stuSemester, int stuGroup, int stuDepartment, int studentId, byte[] imga) {        StuName = stuName;        StuSemester = stuSemester;        StuGroup = stuGroup;        StuDepartment = stuDepartment;        StudentId = studentId;        Imga = imga;    }    public Student() {    }    public String getStuName() {        return StuName;    }    public void setStuName(String stuName) {        StuName = stuName;    }    public int getStuSemester() {        return StuSemester;    }    public void setStuSemester(int stuSemester) {        StuSemester = stuSemester;    }    public int getStuGroup() {        return StuGroup;    }    public void setStuGroup(int stuGroup) {        StuGroup = stuGroup;    }    public int getStuDepartment() {        return StuDepartment;    }    public void setStuDepartment(int stuDepartment) {        StuDepartment = stuDepartment;    }    public int getStudentId() {        return StudentId;    }    public void setStudentId(int studentId) {        StudentId = studentId;    }    public byte[] getImga() {        return Imga;    }    public void setImga(byte[] imga) {        Imga = imga;    }}