package programable.android.klerart.showuseronrecyclerview.Modell;public class Student {    String StuName,Comment;    int StuSemester,StuGroup,StuDepartment,StudentId,OrginalId,Count;    byte[] Imga;    public String getStuName() {        return StuName;    }    public void setStuName(String stuName) {        StuName = stuName;    }    public String getComment() {        return Comment;    }    public void setComment(String comment) {        Comment = comment;    }    public int getStuSemester() {        return StuSemester;    }    public void setStuSemester(int stuSemester) {        StuSemester = stuSemester;    }    public int getStuGroup() {        return StuGroup;    }    public void setStuGroup(int stuGroup) {        StuGroup = stuGroup;    }    public int getStuDepartment() {        return StuDepartment;    }    public void setStuDepartment(int stuDepartment) {        StuDepartment = stuDepartment;    }    public int getStudentId() {        return StudentId;    }    public void setStudentId(int studentId) {        StudentId = studentId;    }    public int getOrginalId() {        return OrginalId;    }    public void setOrginalId(int orginalId) {        OrginalId = orginalId;    }    public int getCount() {        return Count;    }    public void setCount(int count) {        Count = count;    }    public byte[] getImga() {        return Imga;    }    public void setImga(byte[] imga) {        Imga = imga;    }}