e[] imageViewToByte(ImageView image) {        Bitmap bitmap = ((BitmapDrawable)image.getDrawable()).getBitmap();        ByteArrayOutputStream stream = new ByteArrayOutputStream();        bitmap.compress(Bitmap.CompressFormat.PNG, 100, stream);        byte[] byteArray = stream.toByteArray();        return byteArray;    }    ///////////////////}public class SearchAdapter extends RecyclerView.Adapter<SerachViewHolder>{    private Context context;    private List<Student> students;    public SearchAdapter(Context context, List<Student> students) {        this.context = context;        this.students = students;    }    @NonNull    @Override    public SerachViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        LayoutInflater inflater=LayoutInflater.from(parent.getContext());        View itemView=inflater.inflate(R.layout.my,parent,false);        return new SerachViewHolder(itemView);    }    @Override    public int getItemCount() {        return students.size();    }    @Override    public void onBindViewHolder(@NonNull SerachViewHolder holder, final int position) {        holder.SName.setText("الاسم:" + students.get(position).getStuName());        //   holder.SDepartment.setText(""+students.get(position).getStuDepartment());        holder.SGroup.setText("" + students.get(position).getStuGroup());        if (students.get(position).getStuGroup() == 0)            holder.SGroup.setText("المجموعة الاولي");        else if (students.get(position).getStuGroup() == 1)            holder.SGroup.setText("المجموعة التانية");        else if (students.get(position).getStuGroup() == 2)            holder.SGroup.setText("المجموعة الثالثة");        else if (students.get(position).getStuGroup() == 3)            holder.SGroup.setText("المجموعة الربعة");        holder.itemView.setOnClickListener(                new View.OnClickListener() {                    @Override                    public void onClick( View